output:
.icon {
    width: 64px;
    min-height: 64px;
    text-align: center;
    margin: 5px;
    cursor: pointer;
    position: relative;
    display: flex;
    flex-direction: column;
}

.icon .emoji {
    display: inline-block;
    font-size: 30px;
    position: relative;
}

.icon .title {
    display: block;
    font-size: 8px;
    font-family: monospace;
    color: black;
    padding: 5px;
    word-wrap: break-word;
    border: 1px solid black;
    background-color: white;
    border-radius: 5px;
}

.icon .app-emoji {
    position: absolute;
    right: 12px;
    top: 20px;
}

.icon input[type="checkbox"] {
    position: absolute;
    left: 2px;
    top: -6px;
    width: 8px;
    height: 8px;
}
