#desktop_notifications {
    position: absolute;
    width: 100%;
    height: 100%;
    overflow-y: hidden;
    padding: 10px;
    display: flex;
    flex-wrap: wrap;
    flex-direction: column;
    align-content: flex-start;
}

.notification {
    width: 100%;
    padding: 10px;
    margin: 5px;
    border-radius: 5px;
    background-color: #f1f1f1;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
