output:
.output-wrapper {
    position: fixed;
    top: 0;
    right: 0;
    width: 40%;
    max-width: 500px;
    height: 100vh; /* viewport height */
}

.output {
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5); /* semi-transparent black */
    color: white; /* white text */
    white-space: pre-wrap; /* text wrap */
    font-size: 12px;
    padding: 10px; /* padding */
    margin: 0; /* no margins */
    overflow: auto; /* scroll if content overflows */
}
